mapper.select:
select EXISTS(
	select * 
	  from tb_attendance
	 where member_no = #{memberNo}
   and attend_reg_date>date_format(curdate(),'%Y-%m-%d') 	
) as success

mapper.update:
update tb_attendance
   set code_no = 20
 where attend_no = #{attendNo}

update tb_attendance
   set check_out = now()
 where member_no = #{memberNo}
